import React, { useEffect, useState } from 'react';
import logo from '../logo.svg';
import Cookies from 'js-cookie';
import $ from 'jquery';
import './MainContainer.css';
import Datetime from 'react-datetime';
import "react-datetime/css/react-datetime.css";
import moment from 'moment';

var key = 0;

const userid = Cookies.get('userid');
const username = Cookies.get('username');
console.log({ username: username, userid: userid })

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.o = props;
  }

  showTime() {

  }
  startBooking() {

  }

  render() {
    return (
      <div key={key++}>
        {this.o.text}<input id={this.o.id || null} key={key++} className={this.o.className || "form-control"} name={this.o.name} type={this.o.type || "text"} value={this.o.value} />
      </div>
    )
  }
}
class Hidden extends Input {
  constructor(props) {
    super({
      name: props.name,
      value: props.value,
      type: "hidden"
    })
  }
}

class Button extends React.Component {
  constructor(props) {
    super(props);
    this.o = props;
  }

  render() {
    return (
      <button className="button" onclick={this.props.action} t={this.props.type || "main"}>{this.props.text || "Action"}</button>
    )
  }
}



async function GetOneFrom(url, action) {
  return fetch(url, {
    headers: { "Access-Control-Allow-Origin": "*" }
  })
    .then((response) => {
      if (response.status === 200) {
        return response.json()
      }
    }).then((data) => {
      if (data) {
        action && action(data);
      }
    })
}
async function GetAllFrom(url, action) {
  return fetch(url, {
    headers: { "Access-Control-Allow-Origin": "*" }
  })
    .then((response) => {
      if (response.status === 200) {
        return response.json()
      }
    }).then((data) => {
      if (data) {
        var result = []
        for (var i in data)
          result.push(data[i]);
        action && action(result);
      }
    })
}
function CheckList(list, msg) {
  return (list && list.length > 0) ? false : (<div className="empty">{msg}</div>)
}

function Front() {
  return (
    <div id="front">
      <h3>Welcome</h3>
      <p>We do business here.</p>

      <input className="form-control" id="time" />
      <input className="form-control" id="datetime1" />


      <div className="form-group">
        <div className="input-group date" id='datetime'>
          <input type='text' className="form-control" />
          <span className="input-group-addon">
            <span className="glyphicon glyphicon-calendar"></span>
          </span>
        </div>
      </div>
    </div>
  );
}
class Icon extends React.Component {
  constructor (props){
    super(props)
    this.name = this.props.name || "person"
  }
  render(){
    return(
      <span className="material-symbols-rounded">{this.name}</span>
    )
  }
}
class Venue extends React.Component {
  constructor(props) {
    super(props);
    this.id = props.id || null;
    this.o = props;
    this.showTime = this.showTime.bind(this);
    this.startBooking = this.startBooking.bind(this);
  }

  showTime() {

  }
  startBooking() {

  }

  render() {
    if (this.id)
      return (
        <div key={key++} ishidden="false" className="block">
          <div className="img"><img className="rounded" src={"http://localhost:8088/img/venue/" + this.id + ".jpg"} /></div>

          <div className="info">

            <div className="name"><p>{this.o.name}</p></div>
            <div className="address"><Icon name="pin_drop"/><p>{this.o.address}</p></div>
            <div className="type"><p>{this.o.type}</p></div>
            <div className="staffname"><Icon name="support_agent"/><p>{this.o.staffname}</p></div>
            <div className="desc"><Icon name="description"/><p>{this.o.desc}</p></div>
            <div className="capacity"><Icon name="group"/><p>{this.o.capacity}</p></div>
            <div className="fee"><Icon name="attach_money"/><p>{this.o.fee}</p></div>

            <div className="book button" t="main" onClick={this.startBooking()}><span className="material-symbols-rounded">in_home_mode</span>
              <p>Book</p>
            </div>

            <div className="button time" t="main" onClick={this.showTime()}><span className="material-symbols-rounded">event_available</span>
              <p>View Opening Hours</p>
            </div>

          </div>

        </div>
      );
    else
      return (<div></div>);
  }
}
function Venues() {

  const [venues, setVenue] = useState();
  useEffect(() => {
    GetAllFrom("http://localhost:8088/venue/ALL", setVenue)
  }, []);

  return (
    <div id="venue" className="">
      <h3>Our Venues</h3>
      <div className='venue'>
        {CheckList(venues, "There are no avalible venues.") || venues.map((object) => {
          return new Venue(object).render()
        })}
      </div>
    </div>
  );
}

//no
function Login() {
  return (
    <div id="login">
      <div>
        <h3>Login</h3>
      </div>

      <form method="post" action="request?">
        <input type="hidden" name="action" value="login" />
        <div>
          Email Address:<input className="form-control" name="email" type="email" />
        </div>
        <div>
          Password:<input className="form-control" type="password" name="pwd" />
        </div>
        <input type="submit" value="login" />
      </form>
    </div>
  )
}

function Booking({ venueid }) {

  const [venue, setVenue] = useState()
  const [guests, setGuest] = useState();
  useEffect(() => {
    GetAllFrom("http://localhost:8088/guest/ALL/" + userid, setGuest).then(
      () => GetOneFrom("http://localhost:8088/venue/" + venueid, setVenue)
    )
  }, []);

  class Guest extends React.Component {
    constructor(props) {
      super(props);
      this.editGuest = this.editGuest.bind(this)
    }

    editGuest() {

    }

    render() {
      if (!this.props.simple)
        return (
          <tr key={this.props.key}>
            <th scope="row">{this.props.key}</th>
            <td>{this.props.name}</td>
            <td>{this.props.email}</td>
            <td>
              <Button text="Edit" action={this.editGuest} />
            </td>
          </tr>)
      else
        return (
          <div key={key++} className="list-group-item list-group-item-action">
            {this.props.name}
            <br />
            {this.props.email}
          </div>
        )
    }
  }
  class GuestList extends React.Component {
    constructor(props) {
      super(props);
      this.count = 1;
    }

    render() {
      return (
        <div id={this.props.id || "guest"} className={this.props.simple && "bookingGuest"}>
          {!this.props.simple && (<h3>Guest List</h3>)}

          {!this.props.simple ?
            (
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col"></th>
                    <th scope="col">Name</th>
                    <th scope="col">Email</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {CheckList(guests, "There are no avalible guest.") || guests.map((object) => {
                    object.key = this.count++;
                    return new Guest(object).render()
                  })}
                </tbody>
              </table>
            ) : (
              <div className="list-group">
                {CheckList(guests, "There are no avalible guest.") || guests.map((object) => {
                  object.simple = true;
                  return new Guest(object).render()
                })}
              </div>
            )
          }

          {!this.props.simple && (
            <div className="options">
              <input className="button" t="main" type="submit" value="Create" onclick="openPrompt('createGuest');" />
            </div>
          )}

        </div>
      )
    }
  }

  var yesterday = moment().subtract(1, 'day');
  var nextYear = moment().endOf("year")
  var valid = function (current) {
    var inRange = current.isAfter(yesterday) && current.isBefore(nextYear)
    var closedDates = venue.close
    for(var d in closedDates){
      
      console.log(d)

      var from = moment(d.from)
      var to = moment(d.to)
      inRange = inRange && (current.isBefore(from) || current.isAfter(to))
    }
    return inRange
  };

  var test = new Input({ text: "Tests", name: "test" })

  return (
    <div id="createbooking">
      <h3>Create Booking</h3>
      <div className='venue booking'>
      {new Venue(venue || {key:1}).render()}
      </div>
      <form method="POST" action="request?">
        <input type="hidden" name="action" value="createbooking" />

        <div className="bookingGuestContainer">
          <div>
            Your Guests:
            <GuestList id="bookingGuestLeft" simple="true" />
          </div>
          <div>
            Those you want to invite:
            <div id="bookingGuestRight" className="bookingGuest">
              <div className='list-group'></div>
            </div>
          </div>
        </div>

        <div>
            Date: <Datetime isValidDate={valid} dateFormat="MM-DD" timeFormat={false}/>
        </div>
        <div>
            Time: <Datetime isValidDate={valid} dateFormat={false} timeFormat="hh A"/>
        </div>
        <div>
          Email Address:<input className="form-control" name="email" type="email" />
        </div>
        <input className="button" t="main" type="submit" value="Create Booking" />
      </form>
    </div>
  );
}

export default function MainContainer() {

  return (
    <div className="main-container">
      <Booking venueid="1" />
      <Front />
      <Venues />
    </div>
  )
}
