import React, { useEffect, useState } from 'react';
import logo from '../logo.svg';
import Cookies from 'js-cookie';
import $ from 'jquery';
import './MainContainer.css';
import Nav from '../Nav/Nav';
import moment from 'moment';
import { LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterMoment } from '@mui/x-date-pickers/AdapterMoment'
import { DatePicker } from '@mui/x-date-pickers';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';

var key = 0;

const userid = Cookies.get('userid');
const username = Cookies.get('username');
console.log({ username: username, userid: userid })

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.o = props;
  }

  showTime() {

  }
  startBooking() {

  }

  render() {
    return (
      <div key={key++}>
        {this.o.text}<input onChange={this.props.onChange} pattern={this.props.pattern} max={this.props.max} min={this.props.min} id={this.o.id || null} key={key++} className={this.o.className || "form-control"} name={this.o.name} type={this.o.type || "text"} value={this.o.value} />
      </div>
    )
  }
}
class Hidden extends React.Component {
  constructor(props) {
    super(props)
    this.name = props.name || ""
    this.value = props.value || ""
  }
  render() {
    return (
      <input type="hidden" value={this.value} name={this.name} />
    )
  }
}
class Button extends React.Component {
  constructor(props) {
    super(props);
    this.o = props;
  }

  render() {
    return (
      <button className="button" onClick={this.props.action} t={this.props.type || "main"}>{this.props.text || "Action"}</button>
    )
  }
}
class Icon extends React.Component {
  constructor(props) {
    super(props)
    this.name = this.props.name || "person"
  }
  render() {
    return (
      <span className="material-symbols-rounded">{this.name}</span>
    )
  }
}

$.postJSON = function (url, data, callback) {
  return $.ajax({
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
      "Access-Control-Allow-Origin": "*"
    },
    'type': 'POST',
    'url': url,
    'data': JSON.stringify(data),
    'dataType': 'json',
    'success': callback
  });
};
async function GetOneFrom(url, action) {
  return fetch(url, {
    headers: { "Access-Control-Allow-Origin": "*" }
  })
    .then((response) => {
      if (response.status === 200) {
        return response.json()
      }
    }).then((data) => {
      if (data) {
        action && action(data);
      }
    })
}
async function GetAllFrom(url, action, a2) {
  return fetch(url, {
    headers: { "Access-Control-Allow-Origin": "*" }
  })
    .then((response) => {
      if (response.status === 200) {
        return response.json()
      }
    }).then((data) => {
      if (data) {
        var result = []
        for (var i in data){
          data[i].action = a2
          result.push(data[i])
        }
        action && action(result);
      }
    })
}
function CheckList(list, msg) {
  return (list && list.length > 0) ? false : (<div className="empty">{msg}</div>)
}

function Front() {
  return (
    <div id="front">
      <h3>Welcome</h3>
      <p>We do business here.</p>
    </div>
  );
}
class Venue extends React.Component {
  constructor(props) {
    super(props);
    this.id = props.id || null;
    this.o = props;
  }

  render() {
    if (this.id)
      return (
        <div key={key++} ishidden="false" className="block">
          <div className="img"><img className="rounded" src={"http://localhost:8088/img/venue/" + this.id + ".jpg"} /></div>

          <div className="info">

            <div className="name"><p>{this.o.name}</p></div>
            <div className="address"><Icon name="pin_drop" /><p>{this.o.address}</p></div>
            <div className="type"><p>{this.o.type}</p></div>
            <div className="staffname"><Icon name="support_agent" /><p>{this.o.staffname}</p></div>
            <div className="desc"><Icon name="description" /><p>{this.o.desc}</p></div>
            <div className="capacity"><Icon name="group" /><p>{this.o.capacity}</p></div>
            <div className="fee"><Icon name="attach_money" /><p>{this.o.fee}</p></div>

            <div className="book button" t="main" onClick={this.toBooking(this.o.id)}><span className="material-symbols-rounded">in_home_mode</span>
              <p>Book</p>
            </div>

            <div className="button time" t="main" onClick={this.toBooking(this.o.id)}><span className="material-symbols-rounded">event_available</span>
              <p>View Opening Hours</p>
            </div>

          </div>

        </div>
      );
    else
      return (<div></div>);
  }
}
function Venues({ toBooking }) {
  const [venues, setVenue] = useState();
  useEffect(() => {
    GetAllFrom("http://localhost:8088/venue/ALL", setVenue, toBooking).then(
      console.log(venues)
    )
  }, []);

  return (venues && (
    <div id="venue" className="">
      <h3>Our Venues</h3>
      <div className='venue'>
        {CheckList(venues, "There are no avalible venues.") || venues.map((object) => {
          return new Venue(object).render()
        })}
      </div>
    </div>
  )) || (<div>Loading</div>)
}

//no
function Login() {
  return (
    <div id="login">
      <div>
        <h3>Login</h3>
      </div>

      <form method="post" action="request?">
        <input type="hidden" name="action" value="login" />
        <div>
          Email Address:<input className="form-control" name="email" type="email" />
        </div>
        <div>
          Password:<input className="form-control" type="password" name="pwd" />
        </div>
        <input type="submit" value="login" />
      </form>
    </div>
  )
}

class Guest extends React.Component {
  constructor(props) {
    super(props);
    this.editGuest = this.editGuest.bind(this)
  }

  editGuest() {

  }

  render() {
    if (!this.props.simple)
      return (
        <tr key={this.props.key}>
          <th scope="row">{this.props.key}</th>
          <td>{this.props.name}</td>
          <td>{this.props.email}</td>
          <td>
            <Button text="Edit" action={this.editGuest} />
          </td>
        </tr>)
    else
      return (
        <div key={key++} className="list-group-item list-group-item-action">
          <Hidden name="id" value={this.props.id} />
          {this.props.name}
          <br />
          {this.props.email}
        </div>
      )
  }
}
class GuestList extends React.Component {
  constructor(props) {
    super(props);
    this.count = 1;
    this.guests = props.guests
    this.showEmpty = props.showEmpty || false
  }
  render() {
    return (
      <div id={this.props.id || "guest"} className={this.props.simple && "bookingGuest"}>
        {!this.props.simple && (<h3>Guest List</h3>)}

        {!this.props.simple ?
          (
            <table className="table">
              <thead>
                <tr>
                  <th scope="col"></th>
                  <th scope="col">Name</th>
                  <th scope="col">Email</th>
                  <th scope="col"></th>
                </tr>
              </thead>
              <tbody>
                {CheckList(this.guests, this.showEmpty ? "There are no avalible guest." : "") || this.guests.map((object) => {
                  object.key = this.count++;
                  return new Guest(object).render()
                })}
              </tbody>
            </table>
          ) : (
            <div className="list-group">
              {CheckList(this.guests, this.showEmpty ? "There are no avalible guest." : "") || this.guests.map((object) => {
                object.simple = true;
                return new Guest(object).render()
              })}
            </div>
          )
        }

        {!this.props.simple && (
          <div className="options">
            <input className="button" t="main" type="submit" value="Create" onclick="openPrompt('createGuest');" />
          </div>
        )}

      </div>
    )
  }
}

function Booking({ venueid }) {
  const [venue, setVenue] = useState()
  const [guests, setGuest] = useState();

  const [time, setTime] = useState({ isSet: false })
  const [date, setValue] = useState(moment());

  useEffect(() => {
    GetAllFrom("http://localhost:8088/guest/ALL/" + userid, setGuest).then(
      () => GetOneFrom("http://localhost:8088/venue/" + venueid, setVenue)
    )
  }, []);

  var yesterday = moment().subtract(1, 'day');
  var nextYear = moment().endOf("year")

  var notValidDate = function (date) {
    var notInRange = date.isBefore(yesterday) || date.isAfter(nextYear)
    if (venue && !notInRange) {
      venue.close.forEach(d => {
        var from = moment(d.from)
        var to = moment(d.to)
        notInRange = notInRange || !(date.isBefore(from) || date.isAfter(to))
      })
      var count = 0;
      venue.open.forEach(d => {
        if (d.from < 0) {
          notInRange = notInRange || date.day() == count
        }
        count++
      })
    }
    return notInRange
  }

  var onChange = function (date) {
    var time = { isSet: true };
    if (venue.open) {
      if (venue.open[date.day()]) {
        var t = venue.open[date.day()]
        console.log(t)
        time.start = moment().hour(t.from - 1).minutes(0)
        time.end = moment().hour(t.to - 1).minutes(0)
        time.value = date.hour(time.start.hour() + 1).minutes(0)
      }
    }
    setTime(time)
    setValue(date)
  }

  var onOpen = function (date) {
    setTime({ isSet: false })
  }

  var makeBooking = () => {
    if (time.value) {
      var guests = []
      $("#bookingGuestRight").find('input[name="id"]').each(function () {
        guests.push($(this).val())
      })
      var obj = { guests: guests }
      if (guests.length > 0)
        $.postJSON("http://localhost:8088/booking/guestlist", obj, (res) => {
          obj = res;
          console.log(obj)

          var hour = parseInt($("#createbooking").find('input[name="session"]').val());
          var object = {
            id: "default",
            memberid: userid,
            status: "default",
            createtime: "default",
            image: null,
            guestlistid: obj.id,
            remark: $("#createbooking").find('textarea[name="afewwords"]').val(),
            checkintime: null,
            checkouttime: null,
            startdate:time.value.format('YYYY-MM-DD'),
            starthour:parseInt(time.value.format('HH')),
            totalhour:hour,
            fee:venue.fee*hour,
            template:$("#createbooking").find('select[name="template"]').val()
          }
          $.postJSON("http://localhost:8088/booking/create", object, (res) => {
            console.log(res)
          })
        })
    }
  }

  return ((venue && guests) && (
    <div id="createbooking">
      <h3>Create Booking</h3>
      <div className='venue booking'>
        {new Venue(venue || { key: 1 }).render()}
      </div>
      <input type="hidden" name="action" value="createbooking" />

      <LocalizationProvider dateAdapter={AdapterMoment}>
        <DatePicker
          label="Controlled picker"
          value={date}
          shouldDisableDate={notValidDate}
          onChange={(newValue) => onChange(newValue)}
          onOpen={(newValue) => onOpen(newValue)}
        />
        <TimePicker
          ampm={false}
          label="Controlled picker"
          value={time.value || moment().hour(12).minute(0)}
          minTime={time.start || moment().hour(12)}
          maxTime={time.end || moment().hour(12)}
          timeSteps={{ minutes: 60 }}
          disabled={!time.isSet}
          onChange={(newValue) => setValue(newValue)}
        />
      </LocalizationProvider>
      <Input text="Total Hour" name='session' type="number" />
      <div>

        <div className="bookingGuestContainer">
          <div>
            Your Guests:
            <GuestList id="bookingGuestLeft" guests={guests} showEmpty={true} simple="true" />
          </div>
          <div>
            Those you want to invite:
            <GuestList id="bookingGuestRight" simple="true" />
          </div>
        </div>
        <div>
          Notification Style:
          <select name="template" class="form-select" aria-label="Disabled select example">
            <option defaultValue={true} value="dark">Dark Theme</option>
            <option value="bright">Bright Theme</option>
          </select>
        </div>
        <div>
          Add a few words:<textarea className="form-control" name="afewwords" type="text"></textarea>
        </div>
        <Button type="main" text="Create Booking" action={makeBooking} />
      </div>
    </div>
  )) || (<div>Loading</div>)
}

export default function MainContainer() {

  const[bookingVID, setBVID] = useState();

  const[bh, setB] = useState(false);
  const[vh, setV] = useState(true);
  const[fh, setF] = useState(true);

  function open(func){
    var list = [setB, setV, setF]
    list.forEach((e) => e(false))
    func(true)
  }
  var toBooking = (id) => {
    setBVID(id)
    open(setB)
  }
  var toVenue = () => open(setV)

  return (
    <div id="main">
      <Nav toVenue={toVenue}/>
      <div className="main-container">
        {bh && bookingVID && <Booking venueid={bookingVID} />}
        {fh && <Front/>}
        {vh && <Venues toBooking={toBooking}/>}
      </div>
    </div>
  )
}
